public class WordCount {
    public static void main(String[] args) throws Exception {
        //创建执行环境
        ExecutionEnvironment env = ExecutionEnvironment.getExecutionEnvironment();
        String inputPath = "E:\\Project\\Hadoop\\FlinkTutorialDemo01\\src\\main\\resources\\words.txt";
        DataSet stringDataSource = env.readTextFile(inputPath);

        //对数据集进行处理
        DataSet> dataSet = stringDataSource.flatMap(new MyFlatMapper()).groupBy(0).sum(1);
        dataSet.print();
    }

    public static class MyFlatMapper implements FlatMapFunction>{


        public void flatMap(String s, Collector> collector) throws Exception {
            String[] words = s.split(" ");
            for(String word:words){
                collector.collect(new Tuple2(word,1));
            }
        }
    }
}

